import colors from "../config/colors";
import SafeScreen from "./SafeScreen";

export default function SellingScreen({navigation}) {
  const myItems = [
    {
      id: 1,
      image: require("../assets/user1full.png"),
      name: "user1",
      Item: "plant",
      distance: 10,
      min:1,
      max:4
    },
    {
      id: 2,
      image: require("../assets/user1full.png"),
      name: "user2",
